+    {
+      title: "Food day",
+      items: [
+        "Breakfast/brunch crawl.",
+        "Try 2–3 signature dishes.",
+        "Evening: casual bar/café (optional).",
+      ],
+    },
+  ];
+
+  return Array.from({ length: safeDays }, (_, i) => {
+    const t = templates[i % templates.length];
+    return {
+      day: i + 1,
+      title: t.title,
+      items: t.items,
+    };
+  });
+}
